public class Case2 {
    public static void main(String[] args) {
//        System.out.println(Integer.parseInt("", 2));
        System.out.println(solution("011100"));//7  8
        System.out.println(solution("1111010101111"));//7  8
        System.out.println(solution(""));//7  8
    }
    public static int solution(String S) {
        if (S.length()==0) return 0;
        // write your code in Java SE 8
        char[] chars = S.toCharArray();
        List<Integer> list = new ArrayList<>();
        for (int i = chars.length-1; i >= 0 ; i--) {
            list.add(chars[i]-'0');//大顶端存储在容器低端
        }
        int count = 0;
        while(condition(list)){
            if (check(list)){
                while(list.get(0)==0){
                    //偶数
                    list.remove(0);
                    count++;
                }
            }else{
                while(list.size()>0&&list.get(0)==1){
                    list.remove(0);
                    if (list.size()==1){
                        count+=1;
                    }else{
                        count+=2;
                    }


                }
            }

        }
        return count;
    }

    public static boolean condition(List<Integer> a){
        if (a.size()==0){
            return false;
        }
        for (int i:a){
            if (i==1) return true;
        }
        return false;
    }
    public static boolean check(List<Integer> a){
        if (a.get(0)==1){
            return false;
        }else{
            return true;
        }
    }



}
